import { useState, useEffect } from "react";
let data = [
  {
    firstName: "Gaurav",
    lastName: "Patil",
  },
  {
    firstName: "Prashant",
    lastName: "Patil",
  },
];

const NamesData = () => {
  const [firstName, setFirstName] = useState("");

  const [lastName, setLastName] = useState("");
  const [tableData, setTableData] = useState(data);

  const onSubmitHandler = () => {
    console.log(firstName, lastName);
    let name = {
      firstName: firstName,
      lastName: lastName,
    };

    const names = [...tableData, name];
    setTableData(names);
  };

  // useEffect(()=>{
  //   setTableData(data)
  // }, [data])

  return (
    <>
      <div className="flex justify-center">
        <span>
          <div>

          </div>
         
          <div>
            <table>
              <thead>
                <tr>
                  <th>First Name</th>
                  <th>Last Name</th>
                </tr>
              </thead>
              <tbody>
                {tableData.map((name) => (
                  <tr>
                    <td>{name.firstName}</td>
                    <td>{name.lastName}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </span>
      </div>
    </>
  );
};

export default NamesData;
