import { useState } from "react";


const Calculator = () => {
  const [number1, setNumber1] = useState("")
  const [number2, setNumber2] = useState("")

  const addHandler = (event) => {
     event.preventDefault()
     const result = Number(number1) + Number(number2)
     alert(`Result : ${result}`);
  }

  const subtractHandler = (event) => {
    event.preventDefault()
    console.log('hello')
    const result = Number(number1) - Number(number2)
    alert(`Result : ${result}`);
 }

 const multiplyHandler = (event) => {
    event.preventDefault()
    console.log('hello')
    const result = Number(number1) * Number(number2)
    alert(`Result : ${result}`);
 }

 const divideHandler = (event) => {
    event.preventDefault()
    console.log('hello')
    const result = Number(number1) / Number(number2)
    alert(`Result : ${result}`);
 }

  

  return (
    <div className="flex justify-center">
      <form>
        <div className="flex">
        <label className="block text-gray-700 font-semibold" for="number1">Number 1</label>
        <br />
        <input className="border-co" type="number" id="num1" name="number1" onChange={(event)=> setNumber1(event.target.value)} />
        <br />
        <label for="number2">Number 2:</label>
        <br />
        <input type="number" id="num2" name="number2" onChange={(event)=> setNumber2(event.target.value)}/>
        <br />
        </div>
        
        <input className="mt-2" type="submit" value="Add" onClick={addHandler}/>
        <input type="submit" value="Sub" onClick={subtractHandler}/>
        <input type="submit" value="Multiply" onClick={multiplyHandler}/>
        <input type="submit" value="Divide" onClick={divideHandler}/>
      </form>
    </div>
  );
}

export default Calculator;
